import React, { useEffect } from 'react';
import { connectToMongoDB } from './db';
import SurveyForm from './SurveyForm';

function App() {
  useEffect(() => {
    // Connect to the MongoDB server when the component mounts
    connectToMongoDB();
  }, []);

  return (
    <div className="App">
      <SurveyForm />
    </div>
  );
}

export default App;
