import { text } from 'body-parser';
import mongoose from 'mongoose';
import React from 'react';
import {useState,useEffect } from 'react';
import './style.css';


function SurveyForm() {

  const mongoose = require('mongoose');
const Schema = mongoose.Schema;

const mySchema = new Schema({
  _id: { type: String, required: true },
  question: { type: String, required: true },
  type: { type: String, required: true },
  required: { type: Boolean, required: true }
});

const MyModel = mongoose.model('MyModel', mySchema);

module.exports = MyModel;

  
  const [currentPage, setCurrentPage] = useState(1);
  // eslint-disable-next-line no-unused-vars

  // State for the first page
  const [name, setName] = useState(formData?.name?? '');
  const [age, setAge] = useState(formData?.age?? '');
  const [gender, setGender] = useState(formData?.gender?? '');
  const [residence,setResidence] = useState(formData?.residence?? '');
  const [interests, setInterests] = useState(formData?.interest?? []);
  const [comments, setComments] = useState(formData?.comments?? '');
  const [formData, setFormData] = useState(null);
  const[_questions,setQuestions] = useState([]);


  useEffect(() => {
    fetch('mongodb+srv://bokangmodisebkm:Abc123@cluster0.wxapudj.mongodb.net/test') // Change this to the endpoint for retrieving questions from the database
      .then(response => response.json())
      .then(data => setQuestions(data))
      .then(x => console.log(x))
      .catch(error => console.log(error));
  }, []);

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleAgeChange = (event) => {
    setAge(event.target.value);
  };

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const handleInterestsChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setInterests((prevInterests) => [...prevInterests, value]);
    } else {
      setInterests((prevInterests) =>
        prevInterests.filter((interest) => interest !== value)
      );
    }
  };

  const handleCommentsChange = (event) => {
    setComments(event.target.value);
  };

  const handleResChange = (event) =>{
    setResidence(event.target.value)
  }

  const handleNextPage = (event) => {
    event.preventDefault();
    setCurrentPage((prevPage) => prevPage + 1);
  };

  
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = {
      name,
      age,
      gender,
      residence,
      interests,
      comments,
    };
    setFormData(data);
    setCurrentPage(4);
    console.log(JSON.stringify(data));
    
    
  };

  const renderPageOne = () => {
    const [formData, setFormData] = useState({});
  
    const handleChange = (event) => {
      setFormData({
        ...formData,
        [event.target.name]: event.target.value,
      });
    };
  
    const handleSubmit = (event) => {
      event.preventDefault();
      // handle form submission here
    };
  
    return (
      <form onSubmit={handleSubmit}>
        {mySchema &&
          Object.keys(mySchema).map((key) => (
            <div className="form-group" key={key}>
              <label htmlFor={key}>{mySchema[key].question}</label>
              {mySchema[key].type === "text" && (
                <input
                  type="text"
                  id={key}
                  name={key}
                  value={formData[key] || ""}
                  onChange={handleChange}
                  required={mySchema[key].required}
                  className="form-control"
                />
              )}
              {mySchema[key].type === "number" && (
                <input
                  type="number"
                  id={key}
                  name={key}
                  value={formData[key] || ""}
                  onChange={handleChange}
                  required={mySchema[key].required}
                  className="form-control"
                />
              )}
              {mySchema[key].type === "select" && (
                <select
                  id={key}
                  name={key}
                  value={formData[key] || ""}
                  onChange={handleChange}
                  required={mySchema[key].required}
                  className="form-control"
                >
                  <option value="">Select {mySchema[key].question}</option>
                  {mySchema[key].options.map((option, index) => (
                    <option value={option} key={index}>
                      {option}
                    </option>
                  ))}
                </select>
              )}
            </div>
          ))}
        <button type="submit" className="btn btn-primary">
          Next
        </button>
      </form>
    );
  };
  

const renderPageTwo = () => {
  return (
    <>
    <div className="form-group">
      {_questions.map((question) => (
        <div key={question._id} className="form-group">
          <label>{question.question}</label>
          <input type={question.type} required={question.required} />
        </div>
      ))}
      <button className="btn btn-primary" onClick={handleNextPage}>
        Next
      </button>
      </div>
    </>
  );
};

      
  const renderPageThree = () => {
            
   return(
    <>
    <div className="form-group">
    <label htmlFor="comments">Any comments?</label>
      <div className='comment-group'>
        <textarea id="comments" value={comments} onChange={handleCommentsChange} />
        <button type='submit' onClick={handleSubmit}{...handleNextPage}>Submit</button>
      </div>

      </div>
    </>
   );
  }

  const renderPageFour = () => {
    // Create a CSV string from the survey data
    const csv = `Name,Age,Gender,Interests,Residence,Comments\n${name},${age},${gender},"${interests.join(',')}",${residence},${comments}`;
  
    // Create a Blob object from the CSV string
    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
  
    // Create a URL for the Blob object
    const url = URL.createObjectURL(blob);
  
    // Function to download the CSV file
    const downloadCsv = () => {
      const link = document.createElement('a');
      link.setAttribute('href', url);
      link.setAttribute('download', 'survey.csv');
      link.style.display = 'none';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    };
  
    return (
      <>
        <h2>Responses</h2>
        <p><b>Name: </b>{name}</p>
        <p><b>Age: </b>{age}</p>
        <p><b>Gender: </b>{gender}</p>
        <p><b>Residence: </b>{residence}</p>
        <p><b>Interests: </b>{interests.join(',')}</p>
        <p><b>Comments: </b>{comments}</p>
        <button className="btn btn-primary" onClick={downloadCsv}>
          Download CSV
        </button>
      </>
    );
  };
  
  
return (
  <div>
    {currentPage === 1 && renderPageOne()}
    {currentPage === 2 && renderPageTwo()}
    {currentPage === 3 && renderPageThree()}
    {currentPage === 4 && renderPageFour()}
  </div>
);

}


export default SurveyForm;
