import React,{useState,useEffect} from 'react';
import './style.css';

function SurveyForm() {

  useEffect(() =>{
    fetch("http://localhost:3001/Questions/")
    .then((response) => response.json())
    .then((responseJSON) => {
      const questionsArray = responseJSON.map((question) => question.question);
      setQuestions(questionsArray);
    })

    .catch(error => console.error(error));
  },[]);

  
  const [currentPage, setCurrentPage] = useState(1);

  // State for the first page
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [residence,setResidence] = useState('');

  // State for the second page
  const [interests, setInterests] = useState([]);

  // State for the third page
  const [comments, setComments] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleAgeChange = (event) => {
    setAge(event.target.value);
  };

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const handleInterestsChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setInterests((prevInterests) => [...prevInterests, value]);
    } else {
      setInterests((prevInterests) =>
        prevInterests.filter((interest) => interest !== value)
      );
    }
  };

  const handleCommentsChange = (event) => {
    setComments(event.target.value);
  };

  const handleResChange = (event) =>{
    setResidence(event.target.value)
  }

  const handleNextPage = (event) => {
    event.preventDefault();
    setCurrentPage((prevPage) => prevPage + 1);
  };

  
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = {
      name,
      age,
      gender,
      residence,
      interests,
      comments,
    };
    //setFormData(data);
    setCurrentPage(4);
    console.log(JSON.stringify(data));
    
    
  };

  return (
    <div>
      {questions.map((question, index) => (
        <div className="form-group" key={index}>
          <label htmlFor={`qst-${index}`} className="qst-label">
            {question}
          </label>
          <input type="text" id={`qst-${index}`} className="form-control" />
        </div>
      ))}
    </div>
  );

}


export default SurveyForm;
